import "../css/container.css";
import blogs from "../json/blogs.json";
import React from "react";
import Blog from "./Blog";

export default function Container() {
  blogs.reverse();
  const [thePosts, setThePosts] = React.useState(blogs);

  const [showAny, setShowAny] = React.useState({ show: false });
  const showPost = (idOfPost) => {
    const newPost = blogs.filter((el) => el.id === idOfPost);
    setShowAny({ show: true, post: newPost });
  };
  const nums = [];
  for (let i = 0; i < thePosts.length; i++) {
    nums[i] = str (i + 1);
  }
  console.log(nums);
  if (window.location.href.includes("#")) {

  }
    return showAny.show === false ? (
      <section className="cards-primary">
        <div className="card-container">
          {thePosts.map((items, key) => {
            const text = items.desc.split(" ");
            text.length = 21;
            return (
              <article
                className="card"
                key={key}
                onClick={() => showPost(items.id)}
                id={items.id}
              >
                <a href={items.href} className="card-details">
                  <img
                    src={items.img}
                    loading="lazy"
                    alt={items.title}
                    className="w-full h-48 rounded-tl-md rounded-tr-md"
                  />
                  <div className="card-header">
                    <div className="avatar">
                      <img src={items.authorLogo} alt={items.authorName} />
                    </div>
                    <div className="info">
                      <span className="author-name">{items.authorName}</span>
                      <span className="date">{items.date}</span>
                    </div>
                  </div>
                  <div className="card-footer">
                    <h3>{items.title}</h3>
                    {}
                    <p>
                      {text.join(" ")} <strong>... قرائة المزيد</strong>
                    </p>
                  </div>
                </a>
              </article>
            );
          })}
        </div>
      </section>
    ) : (
      <Blog post={showAny.post} show={setShowAny} />
    );
}
